INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (1,'Pickering','1 - Extrem schlechtes Seeing','Sternenabbildung beträgt andauernd das 2fache des Durchmessers des dritten Beugungsringes - Sternbild hat einen Durchmesser von ca. 13"');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (2,'Pickering','2 - Sehr schlechtes Seeing','Sternenabbildung beträgt bis zum 2fachen des Durchmessers des dritten Beugungsringes - Sternbild hat einen Durchmesser von bis zu ca. 13"');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (3,'Pickering','3 - Schlechtes bis Sehr schlechtes Seeing','Sternenabbildung beträgt den Durchmesser des dritten Beugungsringes - Sternbild hat einen Durchmesser von 6.7" und weiter');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (4,'Pickering','4 - Schlechtes Seeing','Beugungsscheibchen häufig sichtbar - Teilbögen von Beugungsringen können manchmal gesehen werden');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (5,'Pickering','5 - Brauchbares Seeing','Beugungsscheibchen dauernd sichtbar - Teilbögen von Beugungsringen können häufig gesehen werden');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (6,'Pickering','6 - Brauchbares bis gutes Seeing','Beugungsscheibchen dauernd sichtbar - Teilbögen von Beugungsringen können konstant gesehen werden');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (7,'Pickering','7 - Gutes Seeing','Beugungsscheibchen manchmal scharf definiert sichtbar - Beugungsringe können zeitweise als lange Bögen oder auch komplette Ringe gesehen werden');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (8,'Pickering','8 - Gutes bis sehr gutes Seeing','Beugungsscheibchen dauernd scharf definiert sichtbar - Beugungsringe können dauernd als lange Bögen oder auch komplette Ringe gesehen werden; Bild ist aber in Bewegung');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (9,'Pickering','9 - Sehr gutes Seeing','Innerer Beugungsring still, die äusseren Ringe zeitweise still');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (10,'Pickering','10 - Perfektes Seeing','Das komplette Beugungsbild des Sternes steht still');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (11,'Antoniadi','I','Perfektes Bild, ohne die geringste Bildunruhe');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (12,'Antoniadi','II','Leichte Wallungen, aber Phasen der Ruhe, die wenigstens einige Sekunden lang sind');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (13,'Antoniadi','III','Mittelmässige Luftruhe. Auffälliges Bildzittern');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (14,'Antoniadi','IV','Schlechtes Seeing, ständig störendes Wabern');
INSERT INTO tblseeing (see_id,see_scale,see_rating,see_description) VALUES (15,'Antoniadi','V','Sehr schlechtes Seeing, welches kaum das Anfertigen einer grobe Skizze zuläßt');
